mapper.insert:

    SELECT ical FROM calender WHERE iuser = #{iuser} AND created_at = #{recDate}

INSERT INTO calender
(iuser, created_at, ex_kcal)
SELECT #{iuser}, #{recDate}, -#{bmr}
FROM DUAL
WHERE NOT EXISTS (
SELECT created_at
FROM calender
WHERE iuser = #{iuser} AND created_at = #{recDate}
)

mapper.select:
select bmr
from user
where iuser = #{iuser}
