mapper.update:
update user
set usepic = #{usepic}
where iuser = #{iuser}

mapper.select:
select uid, name, age, height, weight, gender, usepic, bmr
from user
where iuser = #{iuser}

mapper.update:
update user
set goal = #{goal}
where iuser = #{iuser}

mapper.select:
select iuser, goal
from user
where iuser = #{iuser}
